using System.Collections; using System.Collections.Generic; using UnityEngine;  public class HitTarget : MonoBehaviour {     private int score;      private SoundManager soundMan;      void Start()     {         soundMan = FindObjectOfType<SoundManager>();     }      private void OnCollisionEnter(Collision collision)     {         if (collision.gameObject.name == "box1(Clone)" || collision.gameObject.name == "box2(Clone)")         {             // Give player 100 points for hitting chimney with a present             if (gameObject.tag == "Chimney")             {                 gameObject.SetActive(false);                 score = score + 100;                 soundMan.houseHit.Play();             }             // Give player 20 points for hitting roof             else if (gameObject.tag == "House")             {                 Destroy(gameObject.GetComponent<BoxCollider>());                 score = score + 20;                 soundMan.houseHit.Play();             }             // Player loses 20 points for hitting a house with a smoking chimney             else if (gameObject.tag == "SmokeyHouse")             {                 Destroy(gameObject.GetComponent<BoxCollider>());                 score = score - 20;                 soundMan.smokeyHouseHit.Play();             }             collision.gameObject.SetActive(false);         }         // Call to update score on the UIPanel         FindObjectOfType<UIPanel>().UpdateScore(score);     } }
